INSERT INTO `wp_posts` VALUES (135,1,'2024-01-27 06:23:11','2024-01-27 06:23:11','<strong>Neurodegenerative Erkrankungen und COVID 19 - Zweite Online-Konferenz der YUVEDO Foundation im Jahr 2021</strong>\r\n\r\nNeurologische Erkrankungen könnten in Folge von COVID-19 beträchtlich zunehmen. Die daraus resultierenden medizinischen, sozialen und wirtschaftlichen Herausforderungen machen entschlossenes Handeln dringend erforderlich. In der 2. YUVEDO Online Konferenz beschäftigen wir uns mit den langfristigen Folgen von COVID-19.\r\n\r\nLesen Sie hier <a href=\"/app/uploads/2024/01/NeurodegenerativeErkrankungenUndCOVID19-pressemitteilung.pdf\" target=\"_blank\" rel=\"noopener\">unsere Pressemitteilung</a>, <a href=\"/app/uploads/2024/01/NeurodegenerativeErkrankungenUndCOVID19-programm.pdf\" target=\"_blank\" rel=\"noopener\">das Programm</a> oder verfolgen Sie <a href=\"https://www.youtube.com/watch?v=eQ7E3s8S7G0\" target=\"_blank\" rel=\"noopener\">die Aufzeichnung der gesamten Konferenz</a> auf YouTube.','Online Konferenz: Neurodegenerative Erkrankungen und COVID 19','','publish','closed','open','','online-konferenz-neurodegenerative-erkrankungen-und-covid-19','','','2024-01-27 06:23:52','2024-01-27 06:23:52','',0,'http://yuvedo.local/?p=135',0,'post','',0);
