INSERT INTO `wp_posts` VALUES (17,1,'2024-01-20 15:52:53','2024-01-20 15:52:53','<strong>Wer wir sind</strong>\r\n\r\nDie Yuvedo Foundation wurde 2019 von Jens Gunter Greve und Dr. Jrg Karenfort ins Leben gerufen, um durch eigene Projekte und Veranstaltungen, Partnerschaften sowie durch Projektfrderung die Lebensbedingungen fr Familien mit Parkinson zu verbessern und einen Beitrag zur Heilung der Erkrankung zu leisten. Wir sind ein kleines, aber wachsendes und engagiertes Team, das sich ber Ihre Kommentare, Fragen oder allgemeinen E-Mails an<a href=\"mailto:info@yuvedofoundation.de\">info@yuvedofoundation.de</a>freut.','Wer wir sind','','trash','closed','closed','','wer-wir-sind__trashed','','','2024-01-29 06:43:23','2024-01-29 06:43:23','',0,'http://yuvedo.local/?page_id=17',0,'page','',0);
