INSERT INTO `wp_posts` VALUES (247,1,'2024-01-29 06:41:42','2024-01-29 06:41:42','Die Yuvedo Foundation wurde 2019 von Jens Gunter Greve und Dr. Jörg Karenfort ins Leben gerufen, um durch eigene Projekte und Veranstaltungen, Partnerschaften sowie durch Projektförderung die Lebensbedingungen für Familien mit Parkinson zu verbessern und einen Beitrag zur Heilung der Erkrankung zu leisten. Wir sind ein kleines, aber wachsendes und engagiertes Team, das sich über Ihre Kommentare, Fragen oder allgemeinen E-Mails an <a href=\"mailto:info@yuvedofoundation.de\">info@yuvedofoundation.de</a> freut.','Wer wir sind II','','inherit','closed','closed','','20-revision-v1','','','2024-01-29 06:41:42','2024-01-29 06:41:42','',20,'http://yuvedo.local/?p=247',0,'revision','',0);
